css:
.rest-client {
  position: relative;
  overflow: auto;
}
.rest-client .btn {
  margin: 5px 0;
}
.rest-client-url {
  width: 100%;
  font-size: 15px;
  font-family: Consolas, 'Courier New';
}
.rest-client-headers,
.rest-client-payload {
  width: 100%;
  font-family: Consolas, 'Courier New';
}
.rest-client-action-btns {
  display: block;
  float: right;
  text-align: right;
}
.rest-client-action-btns .rest-client-clear {
  margin-right: 10px;
  color: #cc0000;
}
.rest-client-action-btns .rest-client-clear:hover {
  color: #ff0000;
}
.rest-client-action-btns .rest-client-send {
  color: #45a815;
}
.rest-client-action-btns .rest-client-send:hover {
  color: #58d51b;
}
.field {
  color: #444444;
  background-color: #ffffff;
  border: 1px solid rgba(159, 159, 159, 0.9);
}
.field.rest-client-user-agent {
  padding: 0 5px;
  margin: 10px 5px;
}
.float-right {
  float: right;
}
.lnk {
  text-decoration: none;
}
.lnk:hover {
  text-decoration: underline;
  cursor: pointer;
}
input[type='text'],
textarea {
  border: 1px solid rgba(159, 159, 159, 0.9);
  border-radius: 2px;
  resize: none;
}
