css:
.atom-ts-documentation > div,
atom-ts-reference > div > div {
  max-height: 180px;
  overflow-y: auto;
}
.atom-ts-documentation {
  position: absolute;
  right: calc(20px);
  background: rgba(236, 236, 236, 0.9);
  border: 1px solid rgba(159, 159, 159, 0.2);
  color: #444444;
  border-radius: 2px;
  width: 40%;
  min-height: 180px;
  max-width: 640px;
  opacity: 0;
  pointer-events: none;
  transition: opacity .2s ease;
}
.atom-ts-documentation ::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}
.atom-ts-documentation ::-webkit-scrollbar-track,
.atom-ts-documentation ::-webkit-scrollbar-corner {
  background: #f4f4f4;
}
.atom-ts-documentation ::-webkit-scrollbar-thumb {
  background: #9f9f9f;
  border-radius: 5px;
  box-shadow: 0 0 1px black inset;
}
.atom-ts-documentation.active {
  opacity: 1;
  pointer-events: auto;
}
.atom-ts-documentation.top {
  top: calc(46px);
}
.atom-ts-documentation.middle {
  top: calc(50% -  90px);
}
.atom-ts-documentation.bottom {
  bottom: calc(36px);
}
.atom-ts-documentation h2 {
  margin: 0;
  line-height: 1.4em;
  margin-top: -0.2em;
}
.atom-ts-documentation p {
  margin-top: 10px;
}
.atom-ts-documentation p > span {
  display: block;
  padding-top: 4px;
}
.atom-ts-documentation .link {
  cursor: pointer;
  color: #000000;
}
.atom-ts-documentation .link:hover {
  text-decoration: underline;
}
